-- Populate departments and municipalities (PostgreSQL)
BEGIN;
DELETE FROM users_municipality;
DELETE FROM users_department;



SELECT setval(pg_get_serial_sequence('users_department','id'), COALESCE(MAX(id), 1)) FROM users_department;
SELECT setval(pg_get_serial_sequence('users_municipality','id'), COALESCE(MAX(id), 1)) FROM users_municipality;
COMMIT;
